function generateRandomValue(){return Math.floor(1e4*Math.random())}function addDataFslightboxAttribute(e){const t=generateRandomValue();e.forEach(e=>{e.setAttribute("data-fslightbox",t)})}const containers=document.querySelectorAll(".addLightbox, .bhld-lightbox, .fh-lightbox");containers.forEach(e=>{addDataFslightboxAttribute(e.querySelectorAll("a"))});const images=document.querySelectorAll("img.singleLightbox, img.bhld-single-lightbox, img.fh-lightbox");images.forEach(e=>{const t=e.closest("a");t&&addDataFslightboxAttribute([t])});const imagesInFigures=document.querySelectorAll("figure.singleLightbox img, figure.bhld-single-lightbox img, figure.fh-lightbox img");imagesInFigures.forEach(e=>{addDataFslightboxAttribute(e.closest("figure").querySelectorAll("a"))});
